// import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
// import MainPage from './MyComponents/landingPage/MainPage';
// import MyProfile from './MyComponents/profile/MyProfile';
import Details from './MyComponents/landingPage/Details';
import Home from './MyComponents/Home';
import {
  BrowserRouter as Router,
  Routes as Switch,
  Route,
  useNavigate
} from "react-router-dom";
import axios from 'axios';

function App() {
  let st = {
    margin: '1%'
  };

  const navigate = useNavigate();

  const validate = (user, pass) => {
    try {
      const token = axios.post ("http://localhost:8800/login", {
        username: user, 
        password: pass
      })
      if (token.data.value)
        navigate ("/Details");
    }
    catch (err) {
      console.log (err);
    }
  }

  return (
    <div style={st}>
    <Router>
      <Home validate={validate} />
      <Switch>
        <Route exact path="/Details" element={<Details />} />
        {/* <Route exact path="/MyProfile" element={<MyProfile />} />
        <Route exact path="/" element={<MainPage />} /> */}
      </Switch>
    </Router>
    </div>
  );
}

export default App;
