import React, { useState } from 'react'
import Button from "react-bootstrap/Button";
import glyphicon from '../images/search.png';
import axios from 'axios';

// import { useNavigate } from 'react-router-dom';

const Login = ({ setToken }) => {
  let st = {
    width: '30%',
    marginBottom: '4%'
  };

  let st1 = {
    width: '45%',
    marginTop: '10%',
    marginLeft: '40%',
    padding: '0',
    fontSize: '150%',
  };

  const [user, setUser] = useState("");
  const [pass, setPass] = useState("");
  // const navigate = useNavigate();

  const submit = async e => {
    e.preventDefault();
    if (!user || !pass) {
      alert("Title or Description cannot be blank");
  }
  else {
    try {
      const res = await axios.post ("http://localhost:8800/login", {
        username: user, 
        password: pass
      })
      if (res.data.value)
      {
        sessionStorage.setItem('token', true);
      }
      } catch (err) {
        console.log (err);
      }
        setUser("");
        setPass("");
    }
    }

  return (
    <>
    <form className='login' method='post'>
        <input type="text" name="user" id="user" onChange={(e) => setUser(e.target.value)} placeholder='Username/Email' pattern='[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$'/>
        <br/>
        <input type="password" name="pass" id="pass" onChange={(e) => setPass(e.target.value)} placeholder='Password' />
        <br/>
        <Button type="submit" className="btn btn-default btn-sm" style={st1} onClick={submit}>
          Find <img src={glyphicon} alt="search glyphicon" style={st} />
        </Button>
    </form>
    </>
  )
}

export default Login
