import React, { useState } from 'react'
import Button from "react-bootstrap/Button";
import glyphicon from '../images/search.png';
import axios from 'axios';
// import { useNavigate } from 'react-router-dom';

const Login = () => {
  let st = {
    width: '30%',
    marginBottom: '4%'
  };

  let st1 = {
    width: '45%',
    marginTop: '10%',
    marginLeft: '40%',
    padding: '0',
    fontSize: '150%',
  };

  // const [userData, setUserData] = useState({
  //   user: "",
  //   pass: ""
  // });
  const [user, setUser] = useState("");
  const [pass, setPass] = useState("");
  // const navigate = useNavigate();

  async function loginUser(credentials) {
    return axios.post('http://localhost:8080/login', {
      method: 'POST',
      body: JSON.stringify(credentials)
    })
      .then(data => data.json())
   }

  const submit = async e => {
    e.preventDefault();
    try {
      const token = await loginUser({
        user,
        pass
      });
      console.log (token.data);
    }
    catch (err) {
      console.log (err);
    }
    
  }

  return (
    <>
    <form className='login' method='post' onSubmit={submit}>
        <input type="text" name="user" id="user" onChange={(e) => setUser(e.target.value)} placeholder='Username/Email' pattern='[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$'/>
        <br/>
        <input type="password" name="pass" id="pass" onChange={(e) => setPass(e.target.value)} placeholder='Password' />
        <br/>
        <Button type="submit" className="btn btn-default btn-sm" style={st1}>
          Find <img src={glyphicon} alt="search glyphicon" style={st} />
        </Button>
    </form>
    </>
  )
}

export default Login
