import React, { useEffect, useState } from "react";
import "../style.css";
import img1 from "../../images/img1.jpg";
// import Button from "react-bootstrap/Button";
import { Link } from "react-router-dom";
import Header from "../Header";
import LeftNav from "../LeftNav";
import axios from "axios";
import Modal from 'react-bootstrap/Modal';
import {Form, Button} from 'react-bootstrap';

const MainPage = () => {
  const [items, setItems] = useState([]);

  useEffect (()=>{
    const getItems = async ()=>{
      try { 
        const res = await axios.post("http://localhost:8800/getItems", {
          uid: sessionStorage.getItem('token')
        });
        setItems (res.data);
      }
      catch (err)
      {
        console.log(err);
      }
    };
    getItems ();
  }, []);

  console.log (items);

  // const blobToImage = (blob) => {
  //   return (resolve => {
  //     const url = URL.createObjectURL(blob)
  //     let img = new Image()
  //     img.onload = () => {
  //       URL.revokeObjectURL(url)
  //       resolve(img)
  //     }
  //     img.src = url
  //     console.log(url);
  //   })
  // }

//   const blobToImage = (blob) => {
//     return (const myFile = new File([blob], 'image.jpeg', {
//       type: blob.type,
//   })
// );
//   }

const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
      <Header />
      <LeftNav />
          {items.map((item)=>(
            
            <div className="itemBlock">
              <Link to="/Details">
              <div className="itemImageLogo">
                <img src={img1} alt="img" />
              </div>
            <div className="itemDesc">
            <h3>{item.name}</h3>
            <h5>{item.location}</h5>
            <p>{item.desc}</p>
          </div>
          {/* <h3>{props.ItemName}</h3> */}
          </Link>
          <div className="itemButtons">
            <Button variant="primary">Update</Button>
            <Button variant="danger" onClick={handleShow}>Remove</Button>

            <Modal backdrop={false} fade={false} show={show} onHide={handleClose} style={{padding: '0%'}} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Remove Item</Modal.Title>
        </Modal.Header>
        <Form>
        <Modal.Body style={{paddingTop: '1%', paddingBottom: '5%', fontSize: '110%'}}>
            Do you really want to remove this Item?
        </Modal.Body>
        <Modal.Footer style={{}}>
          <Button variant="secondary" onClick={handleClose}>
            Cancel
          </Button>
          <Button type="submit" variant="danger" onClick={handleClose}>
            Remove
          </Button>
        </Modal.Footer>
        </Form>
        </Modal>
          </div>
        </div>
      
          ))}
          
    </>
  );
};

export default MainPage;
