// import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
// import MainPage from './MyComponents/landingPage/MainPage';
// import MyProfile from './MyComponents/profile/MyProfile';
import Details from './MyComponents/landingPage/Details';
import Home from './MyComponents/Home';
import {
  BrowserRouter as Router,
  Routes as Switch,
  Route,
} from "react-router-dom";
import axios from 'axios';
import { useState } from 'react';

function App() {
  let st = {
    margin: '1%'
  };
  const [token, setToken] = useState();

  if (!token)
  {
    return <Home setToken={setToken} />
  }

  const validate = (user, pass) => {
    try {
      const res = axios.post ("http://localhost:8800/login", {
        username: user, 
        password: pass
      })
      if (res.data.value){
        setToken (res.data.value);
        <Route exact path='/Details' element={<Details />} />
      }
    }
    catch (err) {
      console.log (err);
    }
  }

  return (
    <div style={st}>
    <Router>
      <Home validate={validate} />
      <Switch>
        <Route exact path="/Details" element={<Details />} />
        {/* <Route exact path="/MyProfile" element={<MyProfile />} />
        <Route exact path="/" element={<MainPage />} /> */}
      </Switch>
    </Router>
    </div>
  );
}

export default App;
